import { generateMnemonic, mnemonicToSeedSync, validateMnemonic } from 'bip39'
import * as crypto from 'crypto'

// Generar una frase semilla segura de 24 palabras
function generateSecureMnemonic() {
  // Usar 256 bits de entrop칤a para m치xima seguridad
  const entropy = crypto.randomBytes(32)
  const mnemonic = generateMnemonic(256)

  // Validar que la frase es correcta
  if (!validateMnemonic(mnemonic)) {
    throw new Error('Mnemonic inv치lido generado')
  }

  return mnemonic
}

// Derivar seed de la frase con passphrase opcional
function deriveSeed(mnemonic, passphrase = '') {
  if (!validateMnemonic(mnemonic)) {
    throw new Error('Mnemonic inv치lido')
  }

  // La passphrase a침ade una capa extra de seguridad
  const seed = mnemonicToSeedSync(mnemonic, passphrase)
  return seed
}

// Ejemplo de uso
const mnemonic = generateSecureMnemonic()
console.log('Frase semilla generada (춰GU츼RDALA EN LUGAR SEGURO!):')
